import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function AppointmentList() {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    async function fetchAppointments() {
      try {
        const res = await axios.get('http://localhost:5001/api/appointments');
        setAppointments(res.data);
      } catch (err) {
        console.error('Error fetching appointments', err);
      }
    }
    fetchAppointments();
  }, []);

  return (
    <div className="mt-4">
      <h2 className="text-lg font-semibold">Scheduled Appointments</h2>
      {appointments.length === 0 ? (
        <p>No appointments found.</p>
      ) : (
        <ul className="space-y-2 mt-2">
          {appointments.map((appt) => (
            <li key={appt.id} className="border p-2 rounded">
              <div><strong>Patient:</strong> {appt.patientId}</div>
              <div><strong>Doctor:</strong> {appt.doctorId}</div>
              <div><strong>Date:</strong> {new Date(appt.date).toLocaleString()}</div>
              <div><strong>Location:</strong> {appt.location}</div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
