import React, { useState } from 'react';
import axios from 'axios';

export default function ScheduleAppointment() {
  const [form, setForm] = useState({ patientId: '', doctorId: '', date: '', location: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5001/api/appointments/schedule', form);
    alert('Appointment Scheduled!');
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-2">
      <input placeholder="Patient ID" onChange={e => setForm({ ...form, patientId: e.target.value })} />
      <input placeholder="Doctor ID" onChange={e => setForm({ ...form, doctorId: e.target.value })} />
      <input type="datetime-local" onChange={e => setForm({ ...form, date: e.target.value })} />
      <input placeholder="Location" onChange={e => setForm({ ...form, location: e.target.value })} />
      <button type="submit">Schedule</button>
    </form>
  );
}
