import React, { useState } from 'react';
import axios from 'axios';

const ScheduleAppointment = ({ onAppointmentScheduled }) => {
  const [patientId, setPatientId] = useState('');
  const [doctorId, setDoctorId] = useState('');
  const [date, setDate] = useState('');
  const [location, setLocation] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // ðŸ”’ Frontend Validation
    if (!patientId.trim() || !doctorId.trim() || !date || !location.trim()) {
      alert('Please fill out all fields before scheduling.');
      return;
    }

    try {
      await axios.post('http://localhost:5001/api/appointments/schedule', {
        patientId,
        doctorId,
        date,
        location,
      });

      // Clear form
      setPatientId('');
      setDoctorId('');
      setDate('');
      setLocation('');

      // Refresh appointments
      onAppointmentScheduled();
    } catch (err) {
      console.error('Error scheduling appointment:', err.message);
      alert('Failed to schedule appointment. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Patient ID"
        value={patientId}
        onChange={(e) => setPatientId(e.target.value)}
      />
      <input
        type="text"
        placeholder="Doctor ID"
        value={doctorId}
        onChange={(e) => setDoctorId(e.target.value)}
      />
      <input
        type="datetime-local"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />
      <input
        type="text"
        placeholder="Location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <button type="submit">Schedule</button>
    </form>
  );
};

export default ScheduleAppointment;
